definition.script:
repoUrl = "https://github.com/buildit/jenkins-pipeline-libraries.git"

stage 'branch selection'
node {
    git poll: false, changelog: false, url: repoUrl, credentialsId: "git-credentials", branch: "master"

    gitUtils = load "lib/git.groovy"
    ui = load "lib/ui.groovy"

    def branch = input(message: "Select Branch", parameters: [ui.dropdown("branch", "Branch", {
        gitUtils.listBranches(repoUrl, "master", "git-credentials")
       })], submitter: null)

    sh("git checkout ${branch}")

    pipeline = readFile("./pipelines/meta-pipeline.groovy")
}

evaluate(pipeline)
